import React, { useState } from 'react'
import { Button, TextField } from '@mui/material'
import axios from 'axios'
//import './signup.css'
import { useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Signup() {

    const [values , setValues] =useState({
        name:'',
        email:'',
        password:''

    })

    const handleInput = (event) => {
        const { name, value } = event.target;
        setValues((prev) => ({
          ...prev,
          [name]: value,
        }));
      };
      const navigate = useNavigate();

      const handleSubmit =(event) =>{
        event.preventDefault();

        axios.post("http://localhost:8080/users" ,values)
        .then(res=>{
            if(res.data.Status==="Success"){
                toast.success('You have been registered Successfully');
                setTimeout(() => {
                    navigate('/')
                  }, 3500);
            }else{
             alert("Error")
            }
        }
            )
      }
  return (
<>    
    <div className='UserInfo'>
      <h2>SignUp Form</h2>
      <form onSubmit={handleSubmit}>
        <div className='Other'>
        <TextField id="FullName" sx={{marginBottom:2}} fullWidth label="FullName" variant="outlined" type='text' required onChange={handleInput} name="name" />
        <TextField id="Email" sx={{marginBottom:2}} fullWidth label="Email" variant="outlined" type='email'  required onChange={handleInput} name="email"/>
        <TextField id="Password" sx={{marginBottom:2}} fullWidth label="Password" variant="outlined" type='Password' required onChange={handleInput} name="password"/>
        <p>Already have an accout? <a href="/">SignIn</a></p>
        <Button variant="contained" color="secondary" type='submit' fullWidth >Submit</Button>
        </div>
      </form>
    </div>
  </>
  )
}
export default Signup;
