import React from 'react'
import { Modal,Button,Form } from 'react-bootstrap';
import { useState } from 'react';
import { useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';



function Adminside() {
    const [showModal, setShowModal] = useState(false);
    const [training,setTraining]=useState("");
    const [skill,setskill]=useState("");
    const [skillcat,setskillcat]=useState("");
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');

    const trainings=[{trainingname:"python",
    value:"py",
    skillcategory:["Critical Thinking","Problem Solving","Design"],
    skilltitle:["pandas","numpy","app development","DataScience"]
},
{
    trainingname:"java",
    value:"java",
    skillcategory:["Problem Solving","Development"],
    skilltitle:["Enterprise appliction","mobile app","games","website development"]
}]

var today=new Date();
console.log(today.getDate()-7);
//var date=today.toString().split(" ")[2];
var date=today.getDate()-7;
var month=today.getMonth()+1;
var year=today.getFullYear();
console.log(date+" "+month+" "+year);






const handleStartDateChange = (e) => {
    setStartDate(e.target.value);
  };

  // Function to handle changes in end date input
  const handleEndDateChange = (e) => {
    setEndDate(e.target.value);
  };

    const handleCloseModal = () => {
        setShowModal(false);
      };
    
      const handleOpenModal = () => {
        setShowModal(true);
      };

  return (
   <>
   <Container>
      <Row>
        <Col sm={2}>




        <a href="/Profile">Profile</a>


<a href="#">Dashboard</a>

<a href="#">Timesheet</a>

<a href="#">Projects</a>



<button className="dropdown-btn">Leave

  <i className="fa fa-caret-down"></i>

</button>

<div className="dropdown-container">

  <a href="#">Link 1</a>

  <a href="#">Link 2</a>

  <a href="#">Link 3</a>

</div>

<a href="#">Work From Home</a>

<button className="dropdown-btn">Approvals

  <i className="fa fa-caret-down"></i>

</button>

<a href="#">Survey</a>

<a href="#">Service Desk</a>

<a href="#">Forms</a>

<a href="#">Travel</a>

<a href="#">Expenses</a>

<button className="dropdown-btn">Settings

  <i className="fa fa-caret-down"></i>

</button>

<button className="dropdown-btn">Control Panel

  <i className="fa fa-caret-down"></i>

</button>

<a href="#">Resourcing</a>

<a href="#">Access Control</a>
<a href="/Learn">L&D</a>
{/* <a href="/"><button type="button" id = "logoutbtn"class="btn btn-danger">Logout</button></a> */}









        </Col>
        <Col sm={10}>




        <Button variant="primary" onClick={handleOpenModal}>
          Create Training
        </Button>
  
        <Modal show={showModal} onHide={handleCloseModal}>
          <Modal.Header closeButton>
            <Modal.Title>Training Details</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Select aria-label="Default select example" value={training} onChange={(e)=>{
                  console.log(e.target.value);
                  setTraining(e.target.value);
              }}>
        {trainings.map((item,index)=>{
          return (<option value={index}>{item.trainingname}</option>)
        })}
      </Form.Select>
  <br></br>
       <Form.Select aria-label="Default select example" value={skill} onChange={(e)=>{
  setskill(e.target.value);
       }}>
        {training !== "" && trainings[training].skilltitle.map((item,index)=>{
          return (<option value={index}>{item}</option>)
        })}
      </Form.Select>
      <br></br>
  
  
      <Form.Select aria-label="Default select example" value={skillcat} onChange={(e)=>{
  setskillcat(e.target.value);
       }}>
        {training !== "" && trainings[training].skillcategory.map((item,index)=>{
          return (<option value={index}>{item}</option>)
        })}
      </Form.Select>
      <br></br>
  
  
      <label>Start Date:</label>
        <input
          type="datetime-local"
          //value={startDate}
          //onChange={(e) => setStartDate(e.target.value)}
         // min={collajed}
        />
        <br />
        <label>End Date:</label>
        <input
          type="datetime-local"
         // value={endDate}
          //onChange={(e) => setEndDate(e.target.value)}
        //  disabled={startDate}
          min={new Date() - 7}
        />

          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" >
            Save
          </Button>
        </Modal.Footer>
      </Modal>









        </Col>
      </Row>
      </Container>
 











    



    




    


   




    
            
 

    </>
  )
}

export default Adminside;
